(17003, 953, 'texte_1', '<ul>\r\n 	<li>Kinésiologue</li>\r\n 	<li>Kinésithérapeutes</li>\r\n 	<li>Éducateur physique</li>\r\n 	<li>Intervenant sportif</li>\r\n 	<li>Physiothérapeute</li>\r\n 	<li>Chiropraticien</li>\r\n 	<li>Nutritionniste</li>\r\n 	<li>Psychologue</li>\r\n 	<li>Ostéopathe</li>\r\n</ul>'),
(17004, 953, '_texte_1', 'field_57681b0e94e5e'),
(17005, 953, 'titre_2', 'Approche scientifique'),
(17006, 953, '_titre_2', 'field_57681b2294e5f'),
(17007, 953, 'texte_2', 'Nous avons à coeur votre avancement professionnel. Nos formations sont basées sur les plus récentes évidences scientifiques tout en étant directement transférables à votre pratique.'),
(17008, 953, '_texte_2', 'field_57681b2794e60'),
(17009, 953, 'titre_3', 'Formateurs renommés'),
(17010, 953, '_titre_3', 'field_57681b3794e61'),
