INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(32117, 1420, 'texte_1', '<ul>\r\n 	<li>Kinésiologue</li>\r\n 	<li>Kinésithérapeute</li>\r\n 	<li>Éducateur physique</li>\r\n 	<li>Intervenant sportif</li>\r\n 	<li>Physiothérapeute</li>\r\n 	<li>Chiropraticien</li>\r\n 	<li>Nutritionniste</li>\r\n 	<li>Psychologue</li>\r\n 	<li>Ostéopathe</li>\r\n 	<li>Thérapeute en</li>\r\n 	<li>réadaptation physique</li>\r\n</ul>'),
(32118, 1420, '_texte_1', 'field_57681b0e94e5e'),
(32119, 1420, 'titre_2', 'Approche scientifique'),
(32120, 1420, '_titre_2', 'field_57681b2294e5f'),
(32121, 1420, 'texte_2', 'Nous avons à coeur votre avancement professionnel. Nos formations sont basées sur les plus récentes évidences scientifiques tout en étant directement transférables à votre pratique.'),
(32122, 1420, '_texte_2', 'field_57681b2794e60'),
(32123, 1420, 'titre_3', 'Formateurs renommés'),
(32124, 1420, '_titre_3', 'field_57681b3794e61'),
(32125, 1420, 'texte_3', '<p style="text-align: left;">Dans l’objectif d’assurer un haut standard de formations, nos formateurs sont des experts passionnés, expérimentés et reconnus par leurs pairs. Vous serez formés par la référence du domaine de formation choisie.</p>'),
(32126, 1420, '_texte_3', 'field_57681b3d94e62'),
(32127, 1420, 'titre_4', 'Formations variées'),
(32128, 1420, '_titre_4', 'field_576826d415f45'),
(32129, 1420, 'texte_4', '<ul>\r\n 	<li>Physiologie de l\'exercice</li>\r\n 	<li>Préparation physique</li>\r\n 	<li>Clientèle spécifique</li>\r\n 	<li>Littératie physique</li>\r\n 	<li>Course à pied</li>\r\n 	<li>Haltérophilie</li>\r\n 	<li>Psychologie</li>\r\n 	<li>Pédagogie</li>\r\n 	<li>Nutrition</li>\r\n 	<li>Pilates</li>\r\n 	<li>Yoga</li>\r\n</ul>\r\n... et plus encore!\r\n\r\nAccréditations disponibles pour les professionnels'),
(32130, 1420, '_texte_4', 'field_576826d415f44'),
