(1756, 121, 'titre_1', 'Formations continues pour professionnels du sport et de la santé'),
(1757, 121, '_titre_1', 'field_57681b0694e5d'),
(1758, 121, 'texte_1', '<ul>\r\n 	<li>Kinésiologue</li>\r\n 	<li>Entraîneur physique</li>\r\n 	<li>Éducateur physique</li>\r\n 	<li>Intervenant sportif</li>\r\n 	<li>Physiothérapeute</li>\r\n 	<li>Chiropraticien</li>\r\n 	<li>Nutritionniste</li>\r\n 	<li>Psychologue</li>\r\n 	<li>Ostéopathe</li>\r\n</ul>'),
(1759, 121, '_texte_1', 'field_57681b0e94e5e'),
(1760, 121, 'titre_2', 'Approche scientifique'),
(1761, 121, '_titre_2', 'field_57681b2294e5f'),
(1762, 121, 'texte_2', 'Nous avons à coeur votre avancement professionnel. Nos formations sont basées sur les dernières évidences scientifiques tout en étant directement transférables à votre pratique.'),
(1763, 121, '_texte_2', 'field_57681b2794e60'),
